const getAdvice = async (concentration) => {
  try {
    const res = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/api/advice`, {
      concentration_percentage: concentration * 100, // from ML service
    });
    return res.data.tips; // array of strings
  } catch (err) {
    console.error(err);
    return ["Could not fetch tips"];
  }
};
